mapper.select:
SELECT ck.id AS id,ck.check_title AS checkTitle,ck.check_object AS checkObject,ck.start_date AS startDate,ck.end_date AS endDate, dic.enum_desc AS 'status',ck.check_basis AS checkBasis,ck.check_content AS checkContent,ck.check_way AS checkWay, ck.create_by AS createBy,ck.create_name AS createName,pn.dept_id AS deptId,pn.person_id AS personId,per.name AS personName,dt.name AS deptName,ck.check_num AS checkNum, rd.id AS recordId,rd.assist_person_id AS assistPersonId,per1.name AS assistPersonName,rd.check_mode AS checkMode,rd.check_mode_id AS checkModeId, rd.check_object_type AS checkObjectType,rd.checked_user_name AS checkedUserName, rd.checked_user_id AS checkedUserId, rd.checked_date AS checkedDate, rd.road_name AS roadName, rd.check_situation AS checkSituation,rd.checked_date as checkDate, rd.unit_name AS unitName, rd.regist_num AS registNum,rd.legal_person AS legalPerson,rd.lng as lng,rd.lat as lat FROM ole_ef_check ck LEFT JOIN ole_ef_check_person pn ON pn.check_id=ck.id LEFT JOIN ole_base_dept_person per ON per.ID= pn.person_id LEFT JOIN ole_base_dept dt ON dt.id= pn.dept_id LEFT JOIN ole_sys_dict dic ON ck.status=dic.enum_value AND dic.type_value='1501' LEFT JOIN ole_ef_check_record rd ON ck.id=rd.check_id LEFT JOIN ole_base_dept_person per1 ON per1.ID= rd.assist_person_id
1=1
AND ck.check_num = #{params.caseNum}
;

	SELECT 
	ck.id AS id,
	ck.check_title AS checkTitle 
    FROM ole_ef_check  ck
    LEFT JOIN ole_ef_check_person pn ON pn.check_id=ck.id


			and pn.dept_id = #{deptId}


			and ck.is_relate = #{isRelate}



			and ck.case_accept = #{caseAccept}


SELECT
	sd.enum_desc as statusName,
	count(ec.id) as count
FROM ole_ef_check ec
LEFT JOIN ole_ef_check_person cp on cp.check_id=ec.id
LEFT JOIN ole_ef_check_record cr on cr.check_id=ec.id
LEFT JOIN ole_sys_dict sd on ec.status = sd.enum_value and sd.type_value = '1501'

	1=1

		AND ec.status = #{params.status}


		AND cp.dept_id = #{params.deptId}


		AND cp.person_id = #{params.coordinator}


		AND (cp.person_id = #{params.personId} 
		or cr.assist_person_id = #{params.personId})
