mapper.select:
SELECT bdp.id , bdp.name, bdp.cert_num certNum, bdp.dept_id deptId, bd.name as deptName, bdp.if_effect isEffect, bdp.tel tel, bdp.picture picture FROM ole_base_dept_person AS bdp LEFT JOIN ole_base_dept as bd on bd.id=bdp.dept_id
bdp.del_flag="0"
AND bdp.cert_num = #{params.certNum}
AND bdp.dept_id = #{params.deptId}

SELECT id id, name name, cert_num certNum, dept_id deptId FROM ole_base_dept_person
1=1
and name =#{param.name}
and cert_num =#{param.certNum}
and dept_id =#{param.deptId}

SELECT dp.id id, dp.name name, dp.cert_num certNum, dp.law_type lawType, d.name deptId FROM ole_base_dept_person dp left join ole_base_dept d on dp.dept_id=d.id
dp.del_flag="0"
and dp.cert_num =#{ping.certNum}
and dp.name like concat('%', #{ping.name}, '%')
and dp.law_type =#{ping.lawType}
and dp.dept_id =#{ping.deptId}

SELECT count(obdp.name) FROM ole_base_dept_person obdp LEFT JOIN ole_base_dept obd ON obd.id = obdp.dept_id and obd.is_ps='1' LEFT JOIN ole_base_area oba ON oba.id = obd.area_id LEFT JOIN ole_sys_dict osd ON obdp.duty = osd.enum_value and osd.type_value = "1706"
obd.del_flag="0" and obdp.del_flag="0" and obd.if_effect="1"
AND oba.name=#{areaName}

SELECT count(obdp.name) FROM ole_base_dept_person obdp LEFT JOIN ole_base_dept obd ON obd.id = obdp.dept_id and obd.is_ps='1' LEFT JOIN ole_base_area oba ON oba.id = obd.area_id LEFT JOIN ole_sys_dict osd ON obdp.duty = osd.enum_value and osd.type_value = "1706"
obd.del_flag="0" and obdp.del_flag="0" and obd.if_effect="1" and obdp.law_type=1
AND oba.name=#{areaName}

SELECT count(obdp.name) FROM ole_base_dept_person obdp LEFT JOIN ole_base_dept obd ON obd.id = obdp.dept_id and obd.is_ps='1' LEFT JOIN ole_base_area oba ON oba.id = obd.area_id LEFT JOIN ole_sys_dict osd ON obdp.duty = osd.enum_value and osd.type_value = "1706"
1=1 and obdp.law_type=1 and obd.del_flag="0" and obdp.del_flag="0" and obd.if_effect="1"
AND obd.id=#{params.deptId}
