mapper.select:
select u.user_id userId, u.username, IFNULL(u.user_work,'') userWork, IFNULL(u.user_head_picture,'') userHeadPicture, u.user_true_name userTrueName, u.user_nickname userNickname, IFNULL(u.mobile,'') mobile from sys_user u where 1=1
and u.is_party_member = 1
and u.is_cadres = 1
and u.user_dept = #{params.userDept}
and u.user_org = #{params.userOrg}
and (u.user_true_name like concat('%',#{params.name},'%') or u.user_nickname like concat('%',#{params.name},'%'))
and (u.is_public = #{params.isPublic} or u.is_public = 3)
limit #{params.page},#{params.limit}

SELECT ser.user_id, ser.user_true_name, ser.user_nickname, ser.mobile, ser.user_org, ser.user_head_picture, org.org_name FROM sys_user ser LEFT JOIN tb_user_org org ON org.org_id = ser.user_org WHERE 1=1
AND ser.user_true_name like concat('%',#{params.userTrueName},'%')
AND ser.user_nickname=#{params.userNickname}
AND ser.mobile=#{params.mobile}
limit #{params.page},#{params.limit}
